parameters.jsCode:
// 1. OBTENER DATOS DE ENTRADA
// ---
// Obtener la malla completa que consultó el nodo MySQL_Get_Malla
const mallaCompleta = $('MySQL_Get_Malla').all();

// Obtener los datos del estudiante
const alumno = $('Webhook').first().json.body;
const aprobadas = alumno.asignaturas_aprobadas || [];
const enCurso = alumno.asignaturas_en_curso || [];
const semestreActual = alumno.semestre_actual;


// 2. LISTAS PARA CLASIFICAR
// ---
let asignaturas_disponibles = [];
let asignaturas_bloqueadas = [];
let asignaturas_criticas = [];


// 3. PROCESAR CADA CURSO DE LA MALLA
// ---
for (const item of mallaCompleta) {
  const curso = item.json; // El objeto del curso (fila de la DB)
  const codigo = curso.codigo_asignatura;

  // A. Omitir cursos ya aprobados/en curso para las listas de recomendación
  // (Nota: No los guardamos en disponibles, pero siguen estando en mallaCompleta)
  if (aprobadas.includes(codigo) || enCurso.includes(codigo)) {
    continue; 
  }

  // B. Revisar prerrequisitos
  let cumplePrerrequisitos = true; 
  let faltantes = []; 

  if (curso.prerrequisitos) {
    const listaReqs = curso.prerrequisitos.split(',');
    for (const req of listaReqs) {
      if (!aprobadas.includes(req)) {
        cumplePrerrequisitos = false;
        faltantes.push(req);
      }
    }
  }

  // C. Clasificar
  if (cumplePrerrequisitos) {
    asignaturas_disponibles.push(curso);
    if (curso.semestre_recomendado == semestreActual) {
      asignaturas_criticas.push(curso);
    }
  } else {
    curso.prerrequisitos_faltantes = faltantes.join(','); 
    asignaturas_bloqueadas.push(curso);
  }
}


// 4. DEVOLVER EL REPORTE FINAL
// ---
return [{
  reporte: {
    alumno: alumno.nombre,
    semestre_objetivo: semestreActual,

    // Listas procesadas
    asignaturas_disponibles: asignaturas_disponibles,
    asignaturas_criticas: asignaturas_criticas,
    asignaturas_bloqueadas: asignaturas_bloqueadas,

    // <--- NUEVO: Agregamos la lista completa sin filtrar
    // Usamos .map() para extraer solo los datos limpios (sin la envoltura de n8n)
    todas_las_asignaturas: mallaCompleta.map(item => item.json) 
  }
}];
